package Arrayexampleassign;

public class sumofallarray {

	public static void main(String[] args) {
		
		
				        // Initialize array
		        int[] arr = {1, 2, 3, 4, 5};
		        int sum = 0;

		        // Loop through the array to calculate the sum of elements
		        for (int i = 0; i < arr.length; i++) {
		            sum += arr[i];
		        }

		        // Print the sum
		        System.out.println("Sum of all the elements of the array: " + sum);
		    }
		

		// TODO Auto-generated method stub

	}


